CREATE DEFINER=`root`@`localhost` FUNCTION `RatingBase` (`urId` BIGINT UNSIGNED) RETURNS FLOAT UNSIGNED READS SQL DATA
    SQL SECURITY INVOKER
begin

DECLARE sumVal BIGINT DEFAULT 0;
DECLARE rProblemsCount BIGINT DEFAULT 0;

