	(
        `userId` = urId
    AND
        `b` >= 0
    AND
        `classworkId` = 0
    AND
        `olympId` = 0
    )
ORDER BY
	`b` DESC
LIMIT
	30
;

RETURN (sumVal / rProblemsCount);

end$$

