INSERT INTO `spm_problems` (`id`, `enabled`, `difficulty`, `category_id`, `name`, `description`, `input_description`, `output_description`, `authorSolution`, `authorSolutionLanguage`, `adaptProgramOutput`) VALUES
(1001, 1, 1, 1, 'Hello, world!', 'Напишіть програму, що виведе у вихідний потік рядок &quot;Hello, world!&quot; і одразу ж завершить свою роботу.', '', 'В вихідний потік вивести фразу \"Hello, world!\".', , 'csharp', 1),
(1002, 1, 1, 1, 'A+B', 'Вивести у стандартний вихідний потік суму A і B.', 'У вхідному потоці надаються 2 цілих числа через пробіл, що знаходяться у проміжку від -32000 до 32000.', 'В вихідний потік вивести суму чисел, що надані у вхідному потоці.', 0x76617220612c623a696e74656765723b0d0a626567696e0d0a20202020726561646c6e28612c62293b0d0a20202020777269746528612b62293b0d0a656e642e, 'freepascal', 1),
(1003, 1, 1, 1, 'C+D', 'Вивести на екран суму речових&amp;nbsp;чисел C и D.', 'У вхідному потоці надано 2 речових числа, що знаходяться у діапазоні від -32000 до 32000.', 'Вивести на екран суму наданих чисел із точністю 3 знаки після точки.', 0x76617220632c643a7265616c3b0d0a626567696e0d0a20202020726561646c6e28632c64293b0d0a2020202077726974652828632b64293a303a33293b0d0a656e642e, 'freepascal', 1),
(1004, 1, 2, 6, 'Надтяжка задача', '&lt;p&gt;Вивести даний у вхідному потоці масив у вихідний потік.&lt;/p&gt;', 'У першому рядку вхідного потоку задано натуральне число N - кількість елементів цілочисельного масиву (N <1000).\r\nУ другому рядку перераховані через пробіл значення елементів масиву Ai (-32000 <Ai <32000)', 'Вивести в стовпчик значення всіх елементів заданого масиву в прямому порядку.', , 'freepascal', 1),
(1005, 1, 2, 6, 'Пошук найменшого', '&lt;p&gt;У вхідному потоці надана послідовність чисел. Знайти в ній найменше число.&lt;/p&gt;', 'Задано спочатку число N (кількість чисел в послідовності), а потім на новому рядку N чисел. Всі числа - з діапазону Integer (|a[i]| <= 32000). N <= 100.', 'Виведіть на екран найменше число.', , 'freepascal', 1),
(1006, 1, 4, 6, 'Вивести одиноких', '&lt;p&gt;Дано два масиви чисел. Потрібно вивести у вихідний файл ті елементи першого масиву (в тому порядку, в якому вони йдуть в першому масиві), яких немає в другому масиві.&lt;/p&gt;', 'У вхідному потоці записано спочатку число N - кількість елементів в першому\r\nмасиві, потім N чисел - елементи масиву. Потім записано число M - кількість\r\nелементів в другому масиві. Потім записані елементи другого масиву.\r\nКількість елементів кожного масиву не перевищує 100. Самі елементи -\r\nчисла з діапазону Longint (|a[i]| <= 2 147 483 647).', 'У вихідний потік виведіть ті елементи першого масиву, яких немає в другому в\r\nтому порядку, в якому вони йдуть в першому масиві.', , 'freepascal', 1),
(1007, 1, 6, 6, 'День народження Sirkadirov-а', '&lt;p&gt;Головний кухар вирішив влаштувати в Гімназії 28 День народження Sirkadirov-а. Для цього він приготував гімназистам N надзвичайно смачних піц і потай ухвалив, що перший, який завітав покуштувати поварську страву школяр повинен отримати найбільшу кількість смачних піц, а кожний наступний - строго менше, ніж попередній (кухареві дуже не подобалося, коли до приготовленого їм обіду спізнювалися і той змушений був остигати).&lt;/p&gt;\r\n&lt;p&gt;Звичайно, введене правило залишає істотне свавілля в числі піц, одержуваних черговим з\'явився гімназистом, і це число не в останню чергу залежатиме від ставлення Sirkadirov-а до гімназиста, а також від чарівних слів, вимовлених ним на честь Sirkadirov-а. Наприклад, 6 піц можуть бути в результаті розподілені по одній з наступних чотирьох схем: 3 + 2 + 1 (три піци першому з тих, хто прийшов школярів, дві - другого і одну - третього), 4 + 2, 5 + 1 та 6 (всі піци з\'їдає щасливчик, який прийшов першим).&lt;/p&gt;\r\n&lt;p&gt;Кухар просить вас написати програму, яка визначить, якою кількістю різних способів він може розподілити приготоване ласощі серед школярів.&lt;/p&gt;', 'Вхідний потік містить одне ціле число N - кількість приготованих\r\nкухарем піц (0 <= N <= 200).', 'Вихідний потік повинен містити одне ціле число типу longint (0 <= a <= 2 147 483 647), що дорівнює кількості можливих розподілів піц.', , 'freepascal', 1),
(1008, 1, 50, 19, 'Регулярні вирази', '&lt;p&gt;Потрібно визначити чи підходить задане слово під заданий шаблон. Шаблон задається великими латинськими буквами, знаками &quot;?&quot; - будь-який символ, &quot;*&quot; - будь-яка послідовність символів (навіть порожня).&lt;/p&gt;', 'У перших двох рядках записані шаблон і слово: в одному рядку записаний шаблон - послідовність великих\r\nлатинських букв, \"?\" і \"*\", в інший - слово, що складається тільки\r\nз великих латинських букв (рядки коротше 100 символів).', 'Вивести YES, якщо слово підходить або NO, якщо немає.', , 'freepascal', 1),
(1009, 1, 2, 3, 'Шахова туга', '&lt;p&gt;Дано координати двох полів шахової дошки (координати клітини - це 2 числа від 1 до 8: номер стовпця і номер рядка) Одного кольору ці клітини на шахівниці чи ні? Вивести у вихідний потік ЄДИНИЙ РЯДОК, що містить YES, якщо вони одного кольору, і NO у протилежному випадку.&lt;/p&gt;', 'У вхідному потоці дано 4 числа типу byte (від 0 до 255).', 'Вивести у вихідний потік\r\nЄДИНИЙ РЯДОК, що містить YES, якщо вони одного кольору, і NO у протилежному випадку.', , 'freepascal', 1),
(1010, 1, 10, 8, 'Розпакування рядків', '&lt;p&gt;Будемо розглядати тільки рядки, що складаються з великих латинських букв. Наприклад, розглянемо рядок AAAABCCCCCDDDD. Довжина цього рядка дорівнює 14. Оскільки рядок складається тільки з латинських букв, що повторюються, символи можуть бути видалені і замінені числами, що визначають кількість повторень. Таким чином, даний рядок може бути представлена як 4AB5C4D. Довжина такого рядка 7. Описаний метод ми назвемо упаковкою рядки. Напишіть програму, яка бере упакований рядок і відновлює по ньому початковий рядок.&lt;/p&gt;', 'Вхідний потік містить один упакований рядок. У рядку можуть зустрічатися тільки конструкції виду nA, де n - кількість повторень символу (ціле число від 2 до 99), а A - заголовна латинська буква, або конструкції виду A, тобто символ без числа, що визначає кількість повторень. Максимальна довжина рядка не перевищує 80 символів.', 'У вихідний потік виведіть відновлений рядок. При цьому рядок повинен бути розбитий на рядки довжиною рівно по 40 символів (за винятком останнього, який може містити менше 40 символів).', , 'freepascal', 1),
(1011, 1, 3, 6, 'Кількість перед парними', '&lt;p&gt;&lt;span lang=&quot;uk&quot;&gt;Знайдіть кількість елементів масиву, після яких знаходиться парне число.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span lang=&quot;uk&quot;&gt;============&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span lang=&quot;uk&quot;&gt;Найдите количество элементов массива, после которых находится четное число.&lt;/span&gt;&lt;/p&gt;', 'У першому рядку дано натуральне число N (1 ≤ N ≤100) - кількість елементів масиву.\r\nУ другому рядку через пропуск перераховані всі елементи масиву (-32000 ≤ a [i] ≤ 32000).\r\n============\r\nВ первой строке дано натуральное число N (1 ≤ N ≤100) - количество элементов массива.\r\nВо второй строке через пробел перечислены все элементы массива (-32000 ≤ a [i] ≤ 32000).', 'Єдине натуральне число.\r\n============\r\nЕдинственное натуральное число', , 'freepascal', 1),
(1012, 1, 3, 6, 'Не додатні сусіди', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;Знайдіть суму елементів масиву, у яких є не додатній сусід.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;================&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;Найдите сумму элементов массива,&amp;nbsp;у которых&amp;nbsp;есть не положительный сосед.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', 'У першому рядку дано натуральне число N (1 ≤ N ≤100) - кількість елементів масиву. У другому рядку через пропуск перераховані всі елементи масиву (-32000 ≤ a [i] ≤ 32000).\r\n================\r\nВ первой строке дано натуральное число N (1 ≤ N ≤100) - количество элементов массива. Во второй строке через пробел перечислены все элементы массива (-32000 ≤ a [i] ≤ 32000).', 'Єдине ціле число.\r\n================\r\nЕдинственное целое число.', , 'freepascal', 1),
(1013, 1, 5, 6, 'Среднее арифметическое непростых', '&lt;p&gt;Найти среднее арифметическое первых k не простых чисел в заданном одномерном массиве. Гарантируется, что в массиве не меньше k простых чисел.&lt;/p&gt;', 'В первой строке дано число N - количество элементов массива (0 < N < 101) и целое число k (0 < k <101).\r\nВо второй строке перечислены элементы массива через пробел (-32000 < а[i] < 32000)', 'Единственное вещественное число с точностью до двух знаков после запятой.', , 'freepascal', 1),
(1014, 1, 3, 6, 'Среднее арифметическое минимумов', '&lt;p style=&quot;text-align: left;&quot;&gt;Найти среднее арифметическое минимумов данного массива.&lt;/p&gt;', 'В первой строке дано число N - количество элементов массива (0 < N < 101) и целое число k (0 < k <101). Во второй строке перечислены элементы массива через пробел (-32000 < а[i] < 32000).', 'Единственное вещественное число с точностью до двух знаков после запятой.', , 'freepascal', 1),
(1015, 1, 3, 6, 'Наибольший элемент', '&lt;p&gt;Найти наибольший элемент числовой последовательности.&lt;/p&gt;', 'Во входном потоке в первой строке задано целое число N - количество элементов целочисленной числовой последовательности (0 < N < 100). Во второй строке через пробел перечислены значения элементов числовой последовательности. Значения элементов по модулю не превышают 30000.', 'В выходной поток вывести единственное целое число.', , 'freepascal', 1),
(1016, 1, 3, 6, 'Индекс первого наибольшего', '&lt;p&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 16px; text-align: justify;&quot;&gt;Найти индекс первого наибольшего элемента числовой последовательности.&lt;/span&gt;&lt;/p&gt;', 'Во входном потоке в первой строке задано целое число N - количество элементов целочисленной числовой последовательности (0 < N < 100). Во второй строке через пробел перечислены значения элементов числовой последовательности. Значения элементов по модулю не превышают 30000.', 'В выходной поток вывести единственное целое число.', , 'freepascal', 1),
(1017, 1, 3, 6, 'Индекс первого наименьшего', '&lt;p&gt;&lt;span style=&quot;color: #333333; font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 16px; text-align: justify;&quot;&gt;Найти индекс первого наименьшего элемента числовой последовательности.&lt;/span&gt;&lt;/p&gt;', 'Во входном потоке в первой строке задано целое число N - количество элементов целочисленной числовой последовательности (0 < N < 100). Во второй строке через пробел перечислены значения элементов числовой последовательности. Значения элементов по модулю не превышают 30000.', 'В выходной поток вывести единственное целое число.', , 'freepascal', 1),
