INSERT INTO `spm_problems` (`id`, `enabled`, `difficulty`, `category_id`, `name`, `description`, `input_description`, `output_description`, `authorSolution`, `authorSolutionLanguage`, `adaptProgramOutput`) VALUES
(1001, 1, 1, 1, 'Hello, world!', '<p>Напишіть програму, що виведе у <strong>вихідний потік</strong> рядок \"Hello, world!\" і одразу ж завершить свою роботу.</p>', '<p><strong>Вхідні дані</strong> не надаються.</p>', '<p>В <strong>вихідний потік</strong> вивести фразу \"Hello, world!\".</p>', , 'csharp', 1),
(1002, 1, 1, 1, 'A+B', 'Вивести у стандартний вихідний потік суму A і B.', 'У вхідному потоці надаються 2 цілих числа через пробіл, що знаходяться у проміжку від -32000 до 32000.', 'В вихідний потік вивести суму чисел, що надані у вхідному потоці.', 0x76617220612c623a696e74656765723b0d0a626567696e0d0a20202020726561646c6e28612c62293b0d0a20202020777269746528612b62293b0d0a656e642e, 'freepascal', 1),
(1003, 1, 1, 1, 'C+D', 'Вивести на екран суму речових&amp;nbsp;чисел C и D.', 'У вхідному потоці надано 2 речових числа, що знаходяться у діапазоні від -32000 до 32000.', 'Вивести на екран суму наданих чисел із точністю 3 знаки після точки.', 0x76617220632c643a7265616c3b0d0a626567696e0d0a20202020726561646c6e28632c64293b0d0a2020202077726974652828632b64293a303a33293b0d0a656e642e, 'freepascal', 1),
(1004, 1, 1, 8, 'Первый символ', '<p>Дана строка, состоящая из заглавных английских букв и цифр. Вывести первый символ строки.</p>', '<p>В единственной строке входного потока дана строка символов, длина которой не превышает 255.</p>', '<p>В выходной поток вывести единственный символ.</p>', 0x76617220733a737472696e673b0d0a2020202020206e3a696e74656765723b0d0a626567696e0d0a20202020726561646c6e2873293b0d0a20202020777269746528735b315d293b0d0a656e642e, 'freepascal', 1),
(1005, 1, 4, 8, 'Буква в окружении', '<p>Определить количество букв, у которых оба соседа - гласные буквы.</p>', '<p>Во входном потоке дана строка, состоящая только из ЗАГЛАВНЫХ букв английского алфавита. Длина строки не превышает 255 символов.</p>', '<p>В выходной поток вывести единственное целое число.</p>', , 'freepascal', 1),
(1006, 1, 3, 8, 'Предыдущая буква', '<p>Определить предудущую заданной в английском алфавите букву. Если дана первая буква, то вывести последнюю.</p>', '<p>Во входном потоке задана единственная заглавная буква английского алфавита.</p>', '<p>В выходной поток вывести единственную букву.</p>', 0x76617220633a636861723b0d0a626567696e0d0a20726561642863293b0d0a2069662028633d27412729207468656e2077726974652028275a27290d0a2020202020656c736520777269746528636872286f72642863292d3129293b0d0a656e642e, 'freepascal', 1),
(1007, 1, 4, 8, 'Только цифры', '<p>Дана строка, состоящая из больших английских букв и цифр. Получить строку, состоящую их цифр исходной строки сохраняя порядок следования.&nbsp;</p><p>Если в строке нет символов-цифр, то вывести \"NO\".</p>', '<p>Во входном потоке задана строка символов состоящая из маленьких букв и цифр.</p>', '<p>В выходной поток вывести строку символов.</p>', , 'freepascal', 1),
(1008, 1, 3, 8, 'Наибольший символ', '<p>Дана строка, состоящая из заглавных английских букв и цифр. Определить наибольший символ. Символ считается большим, если его код больший.</p>', '<p>В единственной строке входного потока дана строка символов, длина которой не превышает 255.</p>', '<p>В выходной поток вывести единственный символ.</p>', , 'freepascal', 1),
(1009, 1, 3, 8, 'Четырёхзначный палиндром', '<p>Требуется написать программу, определяющую, является ли четырехзначное натуральное число N палиндромом, т.е. числом, которое одинаково читается слева направо и справа налево.</p>', '<p>Во входном потоке записано натуральное число N (1000 ≤ N ≤ 9999).</p>', '<p>В выходной поток вывести слово \'YES\', если число N является палиндромом, или \'NO\' - если нет.</p>', , 'freepascal', 1),
(1010, 1, 6, 8, 'Центральный символ', '<p>Строка разбивается на элементы по 5 символов в каждом. Сформировать слово, составленное из вторых символов элементов, содержащих 25-й от начала массива символ.</p>', '<p>Во входном потоке дана единственная строка, состоящая из заглавных английских букви и цифр. Длинна строки - 50 символов.</p>', '<p>В выходной поток вывести единственную строку.</p>', , 'freepascal', 1),
(1011, 1, 4, 8, 'Удалить начальные цифры', '<p>Дана строка. Удалить цифры с начала строки до первой буквы (начальные цифры). Вывести преобразованную строку и количество удаленных символов.</p>', '<p>В единственной строке входного потока дана строка, длинна которой не превышает 255 символов. Строка состоит из маленьких английских букв и цифр.</p>', '<p>В выходной поток вывести преобразованную строку символов и через пробел целое число - количество удаленных символов.<br></p>', , 'freepascal', 1),
(1012, 1, 4, 8, 'Цифры цифры цифры цифры', '<p>Дана строка. Удалить цифры, окруженные с двух строн цифрами и в полученной строке найти сумму индексов символов-цифр.</p>', '<p>В единственной строке входного потока дана строка, длинна которой не превышает 255 символов. Строка состоит из маленьких английских букв и цифр.</p>', '<p>В выходной поток вывести преобразованную строку символов и через пробел целое число - сумму индексов.</p>', , 'freepascal', 1),
(1013, 1, 5, 6, 'Простое сравнение', '<p>Даны два массива чисел. Требуется вывести в выходной файл те элементы&nbsp;<span style=\"font-size: 1rem;\">первого массива (в том порядке, в каком они идут в первом массиве),&nbsp;</span><span style=\"font-size: 1rem;\">которых нет во втором массиве.</span></p>', '<p>Во входном файле записано сначала число N - количество элементов в первом&nbsp;<span style=\"font-size: 1rem;\">массиве, затем N чисел - элементы массива.</span></p><p><span style=\"font-size: 1rem;\">Затем записано число M - количество&nbsp;</span><span style=\"font-size: 1rem;\">элементов во втором массиве. Затем записаны элементы второго массива.</span></p><p><span style=\"font-size: 1rem;\">Количество элементов каждого массива не превышает 100. Сами элементы -&nbsp;</span><span style=\"font-size: 1rem;\">числа из диапазона Longint.</span></p>', '<p>В выходной файл выведите те элементы первого массива, которых нет во втором в&nbsp;<span style=\"font-size: 1rem;\">том порядке, в каком они идут в первом массиве.</span></p>', , 'freepascal', 1),
(1014, 1, 1, 6, 'Наименьшее', '<p>Дана последовательность чисел. Найти в ней наименьшее число.</p>', '<p>Задано сначала число N (количество чисел в последовательности), а затем&nbsp;<span style=\"font-size: 1rem;\">N чисел. Все числа - из диапазона Integer. N&lt;=100</span></p>', '<p>Выведите наименьшее число.</p>', 0x766172206e2c692c612c623a696e74656765723b0d0a626567696e0d0a72656164286e293b0d0a726561642861293b0d0a666f7220693a3d3220746f206e20646f0d0a2020626567696e0d0a2020726561642862293b0d0a2020696620623c61207468656e20613a3d623b0d0a2020656e643b0d0a77726974656c6e2861293b0d0a656e642e, 'freepascal', 1);

