DROP PROCEDURE IF EXISTS `updateRating`$$
CREATE DEFINER=`*`@`localhost` PROCEDURE `updateRating` (IN `urId` BIGINT UNSIGNED)  SQL SECURITY INVOKER
begin

DECLARE sumVal FLOAT DEFAULT 0;
DECLARE rProblemsCount TINYINT DEFAULT 0;

